(function(){var n=(new signalR.HubConnectionBuilder).withUrl("/chathub").build();n.start().catch(n=>console.error(n.toString()));n.on("NewMessage",(n,t)=>{var i=document.createElement("p");i.innerText=n+":"+t;$("#all_messages").append(i)});n.on("GroupChange",n=>{$("#group_members").text(n)});$("#send_message").click(function(){var t=$("#message_text").val();n.invoke("MessageAll",t)});$("#join_group").click(function(){var t=$("#sender_text").val();n.invoke("JoinGroup",t)});$("#leave_group").click(function(){n.invoke("LeaveGroup")})})();